import { Directive, TemplateRef } from '@angular/core';
/**
 * Used for rendering content when there is no data present. To define the no-data template, nest a `<ng-template>` tag
 * with the `kendo<ComponentName>NoDataTemplate` directive inside the component tag.
 *
 * You can use:
 * - The `kendoAutoCompleteNoDataTemplate` directive for the AutoComplete ([see example]({% slug templates_autocomplete %})).
 * - The `kendoComboBoxNoDataTemplate` directive for the ComboBox ([see example]({% slug templates_combobox %})).
 * - The `kendoDropDownListNoDataTemplate` directive for the DropDownList ([see example]({% slug templates_ddl %})).
 * - The `kendoMultiSelectNoDataTemplate` directive for the MultiSelect ([see example]({% slug templates_multiselect %})).
 *
 * @example
 * ```ts
 * _@Component({
 * selector: 'my-app',
 * template: `
 *  <kendo-combobox [data]="listItems">
 *    <ng-template kendoComboBoxNoDataTemplate>
 *      <h4>No data!</h4>
 *    </ng-template>
 *  </kendo-combobox>
 * `
 * })
 * class AppComponent {
 *   public listItems: Array<string> = [];
 * }
 * ```
 */
export class NoDataTemplateDirective {
    constructor(templateRef) {
        this.templateRef = templateRef;
    }
}
NoDataTemplateDirective.decorators = [
    { type: Directive, args: [{
                selector: '[kendoDropDownListNoDataTemplate],[kendoComboBoxNoDataTemplate],[kendoAutoCompleteNoDataTemplate],[kendoMultiSelectNoDataTemplate]'
            },] },
];
/** @nocollapse */
NoDataTemplateDirective.ctorParameters = () => [
    { type: TemplateRef, },
];
